import React from "react";
import Search from "../search/search";
import axios from 'axios';

class City extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            city: [],
            search: '',
            searchBoolean: false
        }
    }
    componentDidUpdate() {
        const url = `https://ctp-zip-api.herokuapp.com/zip/${this.state.search.length > 4 ? this.state.search : nu}`;
        try {
            axios.get(url)
            .then(response => {
                const res = response.data;
                this.setState({city: res})

            } )
        } catch (error) {
            console.log(error);
        }

    }

     onChangeHandler = (event) => {
        this.setState({search: event.target.value})
     }


    render(){
