import React from "react";
import Search from "../search/search";
import axios from "axios";
import Card from "../card/card";

class City extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      city: [],
      search: "",
      searchBoolean: false,
    };
  }
  componentDidUpdate() {
    const url = `https://ctp-zip-api.herokuapp.com/zip/${this.state.search}`;
    try {
      axios.get(url).then((response) => {
        const res = response.data;
        this.setState({ city: res, searchBoolean: true });
      });
    } catch (error) {
      console.log(error);
    }
  }

  onChangeHandler = (event) => {
    this.setState({ search: event.target.value });
  };

  render() {
    let displayCity = null;

    displayCity = this.state.searchBoolean
      ? this.state.city.map((item) => {
          return (
            <Card
              key={item.RecordNumber}
              state={item.City}
              location={item.Location}
              wages={item.TotalWages}
            />
          );
        })
      : null;

    return (
      <div>
        <Search value={this.state.search} change={this.onChangeHandler}  />
        {displayCity}
      </div>
    );
  }
}
export default City;
