import React from "react";
import Search from "../search/search";
import axios from "axios";
import Card from "../card/card";
import "../css/style.css";

class City extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      city: [],
      search: "",
      searchBoolean: false,
    };
  }
  componentDidUpdate() {
    let city = this.state.search.toUpperCase().trim();
    const url = `https://ctp-zip-api.herokuapp.com/city/${city}`;
    try {
      axios.get(url).then((response) => {
        const res = response.data;
        this.setState({ city: res, searchBoolean: true });
      });
    } catch (error) {
      console.log(error);
    }
  }

  onChangeHandler = (event) => {
    this.setState({ search: event.target.value });
  };

  render() {
    let displayCity = null;

    displayCity = this.state.searchBoolean
      ? this.state.city.map((item, index) => {
          return <Card key={index} city={true} zip={item} />;
        })
      : null;

    return (
      <div>
        <div className='ci'>
          <p>Zip Code</p>
        </div>
        <div>
          <Search
            value={this.state.search}
            change={this.onChangeHandler}
            placeholder="Enter City"
          />
        </div>
        <div>{displayCity}</div>
      </div>
    );
  }
}
export default City;
